var runActive = flow.get('runActive')||true;
var alarmTime = flow.get('alarmTime')||0;
var runTime = flow.get('runTime')||0;


start = new Date(msg.payload.start);
stop = new Date(msg.payload.stop);
now = new Date()

//node.warn(now + ' ' + start + ' ' + stop)

if ((now > start && now < stop) && msg.topic == "runTime")
{
  runActive = true;
  runTime = Math.floor((stop - start) / 1000 / 60);
  ActiveTime = ((now - start) / 1000 / 60) - alarmTime
  flow.set('runTime', runTime);
  flow.set('runActive', runActive);
}
else if ((now < start || now > stop) && msg.topic == "runTime")
{
  runActive = false;
  runTime = 0;
  alarmTime = 0;
  ActiveTime = 0;
  flow.set('runTime', runTime);
  flow.set('alarmTime', alarmTime);
  flow.set('runActive', runActive);
}


if (msg.topic == "alarmTime")
{
    alarmTime = alarmTime + Math.floor(( (stop - start)) / 1000 / 60);
    flow.set('alarmTime', alarmTime);
    node.warn("Alarm time :" + alarmTime + ', Run time : ' + runTime)
}

var newMsg = { payload:"schedule" };
newMsg.cycleTime = msg.payload.cycleTime
newMsg.runActive = runActive;
newMsg.PlannedProductionTime = runTime;
newMsg.ActiveTime = ActiveTime
newMsg.AlarmTime = alarmTime;
newMsg.topic = 'time'
return newMsg;

/*
Availability
Availability takes into account all events that stop planned production long enough where it makes sense to track a reason for being down (typically several minutes).

Availability is calculated as the ratio of Run Time to Planned Production Time:

Availability = Run Time / Planned Production Time

Run Time is simply Planned Production Time less Stop Time, where Stop Time is defined as all time where the manufacturing process was intended to be running but was not due to Unplanned Stops (e.g., Breakdowns) or Planned Stops (e.g., Changeovers).

Run Time = Planned Production Time − Stop Time
*/

